var User=require("../models/users"),jwt=require("jwt-simple"),_=require("lodash"),config=require("../config"),today=new Date,exp=new Date(today);exp.setDate(today.getDate()+60),exports.login=function(e,s){var t=e.body;User.findOne({email:t.email},function(o,i){if(o)s.status(500).json(o);else if(i)if(t.password===i.password){var n=jwt.encode({email:i.email,uuid:i._id,exp:parseInt(exp.getTime()/1e3)},config.secret);e.session.user=i._id,s.status(200).json(n)}else s.status(500).json({msg:"Incorrect password."});else s.status(500).json({msg:"Incorrect Email."})})},exports.signup=function(e,s){var t=new User(e.body);t.save(function(t,o){if(t)s.status(500).json(t);else{var i=jwt.encode({email:o.email,uuid:o._id,exp:parseInt(exp.getTime()/1e3)},config.secret);e.session.user=o._id,s.status(200).json(i)}})},exports.logout=function(e,s){e.session.user=null,s.status(200).json({response:!0})};