var User=require("../models/users"),jwt=require("jwt-simple"),_=require("lodash"),config=require("../config");exports.login=function(e,s){var n=e.body;User.findOne({email:n.email},function(e,n){if(e)s.status(500).json({msg:"Invalid Username or Password, please try again."});else{var r=jwt.encode(n,config.secret);s.status(200).json(r)}})},exports.signup=function(e,s){var n=new User(e.body);n.save(function(e,n){e?s.status(500).json(e):s.status(200).json(n)})};