var User=require("../models/users"),jwt=require("jwt-simple"),_=require("lodash"),config=require("../config"),today=new Date,exp=new Date(today);exp.setDate(today.getDate()+60),exports.login=function(e,s){var t=e.body;User.findOne({email:t.email},function(i,n){if(i)s.status(500).json(i);else if(n||s.status(500).json({msg:"Incorrect username."}),t.password===n.password){var o=jwt.encode({email:n.email,uuid:n._id,exp:parseInt(exp.getTime()/1e3)},config.secret);e.session.user=n._id,s.status(200).json(o)}else s.status(500).json({msg:"Incorrect password."})})},exports.signup=function(e,s){var t=new User(e.body);t.save(function(t,i){if(t)s.status(500).json(t);else{var n=jwt.encode({email:i.email,uuid:i._id,exp:parseInt(exp.getTime()/1e3)},config.secret);e.session.user=i._id,s.status(200).json(n)}})};