var Recipe=require("../models/recipes"),_=require("lodash");exports.index=function(e,n){Recipe.find({inMenu:!0,user:e.session.user}).populate("ingredients").exec(function(e,s){e?n.status(500).json(e):n.status(200).json(s)})},exports.addToMenu=function(e,n){var s=e.body;Recipe.findByIdAndUpdate(s._id,{inMenu:!0},function(e,s){e?n.status(500).json(e):n.status(200).json(s)})},exports.removeFromMenu=function(e,n){var s=e.body;Recipe.findByIdAndUpdate(s._id,{inMenu:!1},function(e,s){e?n.status(500).json(e):n.status(200).json(s)})};