var Recipe=require("../models/recipes"),Ingredient=require("../models/ingredients"),_=require("lodash");exports.index=function(e,n){Recipe.find({inMenu:!0,user:e.session.user}).populate("ingredients").exec(function(e,s){e?n.status(500).json(e):n.status(200).json(s)})},exports.buyItem=function(e,n){var s=e.body;Ingredient.findByIdAndUpdate(s._id,{bought:!s.bought},function(e,s){e?n.status(500).json(e):n.status(200).json(s)})};