var Recipe=require("../models/recipes"),Ingredient=require("../models/ingredients"),_=require("lodash");exports.index=function(e,n){Recipe.find({inMenu:!0}).populate("ingredients").exec(function(e,t){e?n.status(500).json(e):n.status(200).json(t)})},exports.buyItem=function(e,n){var t=e.body;Ingredient.findByIdAndUpdate(t._id,{bought:!t.bought},function(e,t){e?n.status(500).json(e):n.status(200).json(t)})};